class PostComments{constructor(e){this.postId=e,this.postContainer=$(`post-${e}`),this.newCommentForm=$(`#post-comment-${e}`),this.createComment(e)}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o))},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                            <p>\n                                <small>\n                                    <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                                </small>\n                                \n                                ${e.content}\n                                <br>\n                                <small>\n                                    ${e.user.name}\n                                </small>\n\n                                <small>\n                                    <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                        0 Likes\n                                    </a>\n                                </small>\n                            </p>    \n                    </li>`)}deleteComment=function(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove()},error:function(e){console.log(e.responseText)}})}))};convertCommentToAjax=function(){let e=this;$("#comments-list-container>ul>li").each((function(){let t=$(this),n=$(" .delete-comment-button",t);e.deleteComment(n)}))}}